"use strict";
var app = getApp();
Page({
    data: {
        btnStyle: 'btn-bg-blue',
        bgmPlaying: true
    },
    back: function () {
        wx.redirectTo({
            url: "/prepare-pages/pages/rule/rule"
        });
    },
    btnToRed: function () {
        app.sound("btn");
        this.setData({
            btnStyle: 'btn-bg-red'
        });
    },
    btnToBlue: function () {
        this.setData({
            btnStyle: 'btn-bg-blue'
        });
    },
    onShow: function () {
        this.setData({
            bgmPlaying: !app.bgmPaused()
        });
    },
    toggleBGM: function () {
        this.setData({
            bgmPlaying: !this.data.bgmPlaying
        });
        app.toggleBGM();
    }
});
